package concatenation;

public class ConcatenationWithNonString 
{
	public static void main(String[] args)
	{
	     // TODO Auto-generated method stub
	     String text = "The value is: ";
	     int value = 42;
	     
	     // Concatenating a string with a non-string value
	     String result = text + value;
	     System.out.println(result); // Output: The value is: 42
	}
}
