package operators;

public class PostIncrementExample 
{
	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		 int a = 5;
	     int b = a++; // b gets the original value of a, then a is incremented
	     System.out.println("Value of a: " + a); // Output: 6
	     System.out.println("Value of b: " + b); // Output: 5
	}
}
