package operators;

public class arithematic_operator 
{
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a = 40;
		int b = 20;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
	}

}
