package parseX_Methods;

public class ParseFloatExample 
{
	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		 String floatString = "5.75";
		
	     float floatValue = Float.parseFloat(floatString);
	    
	     System.out.println("Parsed Float: " + floatValue); // Output: 5.75
	}

}
